sys_script_include.script:
var GetThreeDaysTimeClass = Class.create();
GetThreeDaysTimeClass.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getThreeDaysTime: function() {
		var openedDate = this.getParameter('sysparm_opened_at');
		var gd = new GlideDateTime(openedDate);
		gd.addDaysLocalTime(3);
// 		return whenNeeded;
		return gd;
    },
    type: 'GetThreeDaysTimeClass'
});
